import { useEffect } from 'react'
import React, { useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom';
const Books = () => {
    const [books, setBooks] = useState([])
    useEffect(()=>{
        const fetchAllBooks = async ()=>{
            try{
                const res = await axios.get("http://localhost:8800/books")
                setBooks(res.data);
            }catch(err){
                console.log(err)
            }
        }
        fetchAllBooks()
    }, [])

    const handleDelete = async (bookdid)=>{
        try{
            await axios.delete("http://localhost:8800/books"+idbooks)
            window.location.reload()

        }catch(err){
            console.log(err)
        }
    }
    return(
        <div>
            <h1>Ehsan Book Shop</h1>
            <div className="books">
                {books.map((book)=>(
                    <div className="book" key={book.idbooks}>
                        {book.cover && <img src={book.cover} alt="" />}
                        <h2>{book.title}</h2>
                        <p>{book.desc}</p>
                        <span>{book.price}</span>
                        <button className='delete' onClick={()=>handleDelete(book.idbooks)}>Delete</button>
                        <button className='update'>Update</button>
                    </div>
                ))}
            </div>
            <button>
                    <Link to="/add">Add new book</Link>
            </button>
        </div>
    )
}

export default Books;
